PACKAGE   const IS
	CONSTANT m: INTEGER:= 10;
	CONSTANT n: INTEGER:= 1023;  -- n= 2^m -1  -size of block
	CONSTANT k: INTEGER:= 1013;  -- BCH code (n,k) -no. of information bits
